import { useState } from "react";
import { useHistory } from "react-router";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";


const Register =() =>{
    const history = useHistory();
    const [org_name,setOrgName] = useState('');
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [contact_number,setContactNumber] = useState('');

    const register = async (e) => {
        e.preventDefault();

        var data = {
            org_name: org_name,
            email: email,
            password: password,
            contact_number: contact_number,
            user_role: 'org',
        }

        // console.log(data);

        ApiFunctions.register(data).then(response => {
            window.alert('Sucess');
            history.push('/admin');
            console.log(response);
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
    return (
        <div className="App">
            <Header />
            <div className="bg-main">
                <div className="container dashboard-headline">
                    <div className="row">
                        <div className="col-md-12">
                            <h3>Register</h3>
                            <nav id="breadcrumb" aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">Register</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="pd-main">
                <div className="container">
                    <div className="profile-content-inner-area">
                        <form action="" method="post">
                            <div className="row">
                                <div className="col-xl-12">
                                    <div className="dashboard-box margin-top-0 margin-bottom-30">
                                        <div className="headline">
                                            <h3>Register</h3>
                                        </div>
                                        <div className="content with-padding padding-bottom-0">
                                            <div className="row">
                                                {/* <input type="hidden" name="org_id" value={org_id}  onChange={(e) => setOrgId(e.target.value)} /> */}
                                                <div className="col-xl-12 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Name</h5>
                                                        <input type="text" name="org_name" className="input-border" value={org_name}  onChange={(e) => setOrgName(e.target.value)} required />
                                                    </div>
                                                </div>
                                                <div className="col-xl-12 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Contact No</h5>
                                                        <input type="text" className="input-border" name="contact_no" value={contact_number}  onChange={(e) => setContactNumber(e.target.value)} required />
                                                    </div>
                                                </div>
                                               
                                                <div className="col-xl-12 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Email</h5>
                                                        <input type="text" name="email" className="input-border" value={email}  onChange={(e) => setEmail(e.target.value)} required />
                                                    </div>
                                                </div>
                                                <div className="col-xl-12 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Password</h5>
                                                        <input type="text" name="password" className="input-border" value={password}  onChange={(e) => setPassword(e.target.value)} required />
                                                    </div>
                                                </div>
                                                
                                                {/* <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Full Name</h5>
                                                        <input type="text" className="input-border" name="name" value="Adam Dale" required />
                                                    </div>
                                                </div> */}
                                               
                                             
                                            </div>
                                            <button type="submit" className="" onClick={register}>Apply Now</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Register;
