import { useEffect, useState } from "react";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import Header from "./Header";
import Sidebar from "./Sidebar";
import useToken from "./useToken";





function Organizations() {
    const history = useHistory();
    const token = useToken();
    if (token.token) {

        if (token.token.data.user_role !== 'admin') {
            history.push('/admin');
        }
    }
    const [list, setList] = useState([]);
    useEffect(() => {
        ApiFunctions.getAllOrg().then(res => {
            var data = res.data.data

            setList(data)
        })
    }, []);

    const Accept = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('approval', 'approved');
        data.append('org_id', e.target.value);

        ApiFunctions.orgStatus(data, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');
            window.location.reload();
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
    const Reject = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('approval', 'rejected');
        data.append('org_id', e.target.value);

        ApiFunctions.orgStatus(data, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');
            window.location.reload();

        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }

    const InActiveStatus = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('status', 'inactive');
        data.append('user_id', e.target.value);

        ApiFunctions.orgChangeStatus(data, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');
            window.location.reload();
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
    const ActiveStatus = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('status', 'active');
        data.append('user_id', e.target.value);

        ApiFunctions.orgChangeStatus(data, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');
            window.location.reload();
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }

    return (
        <div className="App">

            <div id="wrapper">

                <Sidebar />

                {/* // <!-- Content Wrapper --> */}
                <div id="content-wrapper" className="d-flex flex-column">

                    {/* <!-- Main Content --> */}
                    <div id="content">
                        <Header />

                        <div className="container-fluid">

                            {/* <!-- Page Heading --> */}
                            <div className="d-sm-flex align-items-center justify-content-between mb-4">
                                <h1 className="h3 mb-0 text-gray-800">Organizations</h1>
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb">
                                        <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                        <li className="breadcrumb-item active" aria-current="page">organizations</li>
                                    </ol>
                                </nav>
                            </div>

                            <div id="list-details">

                                <div className="table-responsive">
                                    <table className="table table-hover">
                                        <thead>
                                            <tr>
                                                <th scope="col">Sr. No</th>
                                                <th scope="col">Organization</th>
                                                <th scope="col">Email</th>
                                                <th scope="col">Contact No</th>
                                                <th scope="col">Approval</th>
                                                <th scope="col">Status</th>
                                                <th scope="col">Action</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {list.map((data, id) => {
                                                return <tr key={id}>
                                                    <td>{id + 1}</td>
                                                    <td>{data.org_name}</td>
                                                    <td>{data.email}</td>
                                                    <td>{data.contact_number}</td>
                                                    <td>{data.approval}</td>
                                                    <td>
                                                        <div className="row">
                                                            {data.status === "inactive" ?
                                                                <>
                                                                    <div className="col-md-4">
                                                                        <button type="button" className="btn btn-success btn-sm" onClick={ActiveStatus} value={data._id} >Active</button>

                                                                    </div>
                                                                </>
                                                                :
                                                                data.status === "active" ?
                                                                    <>
                                                                        <div className="col-md-4">
                                                                            <button type="button" className="btn btn-danger btn-sm" onClick={InActiveStatus} value={data._id} >In Active</button>

                                                                        </div>
                                                                    </> :
                                                                    <></>

                                                            }
                                                        </div>


                                                    </td>
                                                    <td>
                                                        <div className="row">
                                                            {data.approval === 'pending' ?

                                                                <>
                                                                    <div className="col-md-4">
                                                                        <button type="button" className="btn btn-success btn-sm" onClick={Accept} value={data._id}>Accept</button>

                                                                    </div>
                                                                    <div className="col-md-4">
                                                                        <button type="button" className="btn btn-danger btn-sm" onClick={Reject} value={data._id} >Reject</button>

                                                                    </div>
                                                                </>
                                                                :
                                                                data.approval === 'approved' ?
                                                                    <>
                                                                        <div className="col-md-4">
                                                                            <button type="button" className="btn btn-danger btn-sm" onClick={Reject} value={data._id} >Reject</button>

                                                                        </div>
                                                                    </> :
                                                                    data.approval === 'rejected' ?
                                                                        <>
                                                                            <div className="col-md-4">
                                                                                <button type="button" className="btn btn-success btn-sm" onClick={Accept} value={data._id} >Accept</button>

                                                                            </div>
                                                                        </>
                                                                        :
                                                                        <></>
                                                            }
                                                            
                                                                <div className="col-md-4">

                                                                    <Link to={"/jobs-list/" + data._id} title="Jobs listing"><img src={require('../../imgs/view.jpg').default} width="22" alt="" /></Link>
                                                                </div>
                                                          
                                                            {/* <div className="col-md-4">
                                                                <Link to="/#" ><img src={require('../../imgs/edit.jpg').default} alt="" width="22" /></Link>
                                                            </div>
                                                            <div className="col-md-4">
                                                                <Link to="/#"><img src={require('../../imgs/delete.jpg').default} width="22" alt="" /></Link>
                                                            </div> */}
                                                        </div>
                                                    </td>
                                                </tr>
                                            })}


                                        </tbody>

                                    </table>
                                </div>
                            </div>


                        </div>

                    </div>
                    {/* <!-- End of Main Content --> */}

                    {/* <!-- Footer --> */}
                    <footer className="sticky-footer bg-white">
                        <div className="container my-auto">
                            <div className="copyright text-center my-auto">
                                <span>Copyright Job 2020</span>
                            </div>
                        </div>
                    </footer>
                    {/* <!-- End of Footer --> */}

                </div>
                {/* <!-- End of Content Wrapper --> */}

            </div>
        </div>
    );
