import { useState } from "react";
import {  useHistory } from "react-router";
import ApiFunctions from "../ApiFunctions";
import useToken from "./admin/useToken";
import Header from "./Header";
// import Home from "./home";




// function getToken(history){

  
//     const tokenString = localStorage.getItem('currentUser');
// console.log(tokenString);
// if(tokenString === null || tokenString ===""){
//     // console.log(localStorage.getItem('currentUser'));
//     // history.push('/admin');
// return false;
//     //    return <Redirect to='/admin' />
//     }
//     return JSON.parse(tokenString);
// }
const PostJob =() => {
    // const orgIdParam = useParams();
    const history = useHistory();
    const [job_title,setJobTitle] = useState('');
    const [job_description,setJobDescription] = useState('');
    const [job_type,setJobType] = useState('');
    const [qualification,setQualification] = useState('');
    const [contact_number,setContactNumber] = useState('');
    const [email,setEmail] = useState('');

    // const token = getToken(history);
    const token = useToken();
    console.log(!token.token);
    if(!token.token){
     history.push('/admin');
    }
  
    // const [experience,setExperience] = useState('');

   function handleSelectChange(event) {
     
    setJobType(event.target.value)
     
      }
   function   handleQualificationChange(event) {
    setQualification(event.target.value)
      
      }
      

    const postJob = async (e) => {  
        e.preventDefault();

        var data = {
            org_id: org_id,
            job_title: job_title,
            job_description: job_description,
            job_type: job_type,
            qualification: qualification,
            contact_number: contact_number,
            email: email
        }

        // console.log(data);

        ApiFunctions.postJob(data,{
            headers:{
                'Authorization':token.token.token,
            }
        }).then(response => {
            window.alert('Sucess');
            console.log(response);
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
  
    // console.log(token);
    // const [org_id,setOrgId] = useState('');
    const [org_id,setOrgId] = useState(token.token.data._id);
   
    return (
        <div className="App">
            <Header />
            <div className="bg-main">
                <div className="container dashboard-headline">
                    <div className="row">
                        <div className="col-md-12">
                            <h3>Post Job</h3>
                            <nav id="breadcrumb" aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">Post Job</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="pd-main">
                <div className="container">
                    <div className="profile-content-inner-area">
                        <form action="" method="post">
                            <div className="row">
                                <div className="col-xl-12">
                                    <div className="dashboard-box margin-top-0 margin-bottom-30">
                                        <div className="headline">
                                            <h3>Job Details</h3>
                                        </div>
                                        <div className="content with-padding padding-bottom-0">
                                            <div className="row">
                                                <input type="hidden" name="org_id" value={org_id}  onChange={(e) => setOrgId(e.target.value)} />
                                                <div className="col-xl-12 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Job Title</h5>
                                                        <input type="text" name="job_title" className="input-border" value={job_title}  onChange={(e) => setJobTitle(e.target.value)} required />
                                                    </div>
                                                </div>
                                                <div className="col-xl-12 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Job Description</h5>
                                                        <textarea name="job_desc" className="input-border" rows="3" placeholder="Explain in detail about desired job" value={job_description}  onChange={(e) => setJobDescription(e.target.value)} required></textarea>
                                                    </div>
                                                </div>
                                                {/* <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Full Name</h5>
                                                        <input type="text" className="input-border" name="name" value="Adam Dale" required />
                                                    </div>
                                                </div> */}
                                                <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Email Address</h5>
                                                        <input type="text" className="input-border" name="email" value={email}  onChange={(e) => setEmail(e.target.value)} required />
                                                    </div>
                                                </div>
                                                <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Contact No</h5>
                                                        <input type="text" className="input-border" name="contact_no" value={contact_number}  onChange={(e) => setContactNumber(e.target.value)} required />
                                                    </div>
                                                </div>
                                                <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Job Type</h5>
                                                        <select name="job_type" value={job_type} className="input-border" onChange={handleSelectChange} required>
                                                            <option value="">---select---</option>
                                                            <option value="1">---Test---</option>
                                                            <option value="2">---Test---</option>
                                                        </select>
                                                    </div>
                                                </div>
                                                {/* <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Gender</h5>
                                                        <select name="job_type" className="input-border" required>
                                                            <option value="male">Male</option>
                                                            <option value="female">Female</option>
                                                        </select>
                                                    </div>
                                                </div> */}
                                                <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Qualification</h5>
                                                        <select name="qualification" value={qualification} className="input-border" onChange={handleQualificationChange} required>
                                                            <option value="">---Select---</option>
                                                            <option value="1">---Test---</option>
                                                            <option value="2">---Test---</option>
                                                        </select>
                                                    </div>
                                                </div>
                                            </div>
                                            <button type="submit" className="" onClick={postJob}>Apply Now</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default PostJob;
