//     return <Login />;
//   };

  return (
    <div className="App">
  
   {/* <Home /> */}
    <Switch>
    <Route path="/" exact>
            <Home />
        </Route>  
          <Route path="/PostJob">
              <PostJob />
          </Route>
          <Route path="/browse-companies">
            <BrowseCompanies />
          </Route>
          <Route path="/browse-jobs">
            <BrowseJobs />
          </Route>
          <Route path="/company-details/:id">
            <CompanyDetails />
        </Route>
            <Route path="/job-detail">
            <JobDetails />
        </Route>
           
            <Route path="/about">
            <About />
        </Route>
         <Route path="/admin">
            <Login setToken={setToken} />
        </Route>
         <Route path="/register_company">
            <Register />
        </Route>
         <Route path="/apply-job">
            <ApplyJob />
        </Route>
        </Switch>

    </div>
  );
}

export default App;
