import { useState } from "react";
import { useHistory } from "react-router";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";
import { Helmet } from "react-helmet";

const Register =() =>{
    const history = useHistory();
    const [org_name,setOrgName] = useState('');
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [contact_number,setContactNumber] = useState('');

    const register = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('org_name', org_name);
        data.append('email', email);
        data.append('password', password);
        data.append('contact_number', contact_number);
        data.append('user_role', 'org');

        ApiFunctions.register(data,{
            headers:{
                'content-type':'multipart/form-data'
            }
        }).then(response => {
            window.alert('Success');
            history.push('/admin');
       
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
    return (
        <div className="App">
            <Header />
        
            <Helmet>
				<link rel="stylesheet" href={require('../css/style.css').default} />
			</Helmet>

        <section class="home_1" class="b_img4">
            <div class="container">
                <div class="heading">
                    <h1> Register
                    </h1>
                </div>
            </div>
        </section>
        
        <section class="login_wrapper">
            <div class="container">
                <div class="form_wrap">
                    <h3>Register</h3>
                    <form>
                        <div class="input">
                            <label>First Name</label>
                            <input type="text" value={first_name} onChange={(e) => setFirstName(e.target.value)} placeholder=""/>
                        </div>

                        <div class="input">
                            <label>Last Name</label>
                            <input type="text" value={last_name} onChange={(e) => setLastName(e.target.value)} placeholder=""/>
                        </div>

                        <div class="input">
                            <label>Username</label>
                            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="" />
                        </div>

                        <div class="input">
                            <label>Password</label>
                            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="" />
                        </div>

                        <div class="input">
                            <label>Age</label>
                            <input type="number" value={age} onChange={(e) => setAge(e.target.value)} placeholder=""/>
                        </div>

                        <div class="input">
                            <label>Gender</label>
                            <select value={gender} onChange={(e) => setGender(e.target.value)}>
                                <option value="">Select Gender</option>
                                <option value="Male">Male</option>
                                <option value="Female">Female</option>
                            </select>
                        </div>

                        <div class="input">
                            <label>Contact Number</label>
                            <input type="text" value={contact_number} onChange={(e) => setContact(e.target.value)} placeholder=""/>
                        </div>

                        <div class="input">
                            <label>Email</label>
                            <input type="email" value={email} onChange={(e) => setAge(e.target.value)} placeholder="" />
                        </div>
                        
                        <div class="input">
                            <label>Address</label>
                            <textarea></textarea>
                        </div>

                        <div class="input">
                        </div>


                        <div class="form_btn">
                            <button type="submit" onClick={register} >Register</button>
                        </div>

                    </form>
                </div>
            </div>
        </section>

        </div>
    );
}

export default Register;
