import { useEffect, useState } from "react";
import { useHistory } from "react-router";
import { Link } from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import Header from "./Header";
import Sidebar from "./Sidebar";
import useToken from "./useToken";





function UserList() {
    const history = useHistory();
    const token = useToken();
    if (token.token) {

        if (token.token.data.user_role !== 'admin') {
            history.push('/admin');
        }
    }
    const [list, setList] = useState([]);
    useEffect(() => {
        ApiFunctions.getUsers(null,{
               headers:{
                'Authorization': token.token.token,
               }
        }).then(res => {
            var data = res.data.data

            setList(data)
        })
    }, []);


    return (
        <div className="App">

            <div id="wrapper">

                <Sidebar />

                {/* // <!-- Content Wrapper --> */}
                <div id="content-wrapper" className="d-flex flex-column">

                    {/* <!-- Main Content --> */}
                    <div id="content">
                        <Header />

                        <div className="container-fluid">

                            {/* <!-- Page Heading --> */}
                            <div className="d-sm-flex align-items-center justify-content-between mb-4">
                                <h1 className="h3 mb-0 text-gray-800">Users</h1>
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb">
                                        <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                        <li className="breadcrumb-item active" aria-current="page">Users</li>
                                    </ol>
                                </nav>
                            </div>

                            <div id="list-details">

                                <div className="table-responsive">
                                    <table className="table table-hover">
                                        <thead>
                                            <tr>
                                                <th scope="col">Sr. No</th>
                                                <th scope="col">User</th>
                                                <th scope="col">Email</th>
                                                <th scope="col">Contact No</th>
                                                {/* <th scope="col">Action</th> */}
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {list.map((data, id) => {
                                                return <tr key={id}>
                                                    <td>{id + 1}</td>
                                                    <td>{data.first_name}</td>
                                                    <td>{data.email}</td>
                                                    <td>{data.contact_number}</td>
                                                    <td>
                                                        {/* <div className="row">
                                                            {data.approval === 'pending' ?

                                                                <>
                                                                    <div className="col-md-4">
                                                                        <button type="button" className="btn btn-success btn-sm" onClick={Accept} value={data._id}>Accept</button>

                                                                    </div>
                                                                    <div className="col-md-4">
                                                                        <button type="button" className="btn btn-danger btn-sm" onClick={Reject} value={data._id} >Reject</button>

                                                                    </div>
                                                                </>
                                                                :
                                                                data.approval === 'approved' ?
                                                                    <>
                                                                        <div className="col-md-4">
                                                                            <button type="button" className="btn btn-danger btn-sm" onClick={Reject} value={data._id} >Reject</button>

                                                                        </div>
                                                                    </> :
                                                                    data.approval === 'rejected' ?
                                                                        <>
                                                                            <div className="col-md-4">
                                                                                <button type="button" className="btn btn-success btn-sm" onClick={Accept} value={data._id} >Accept</button>

                                                                            </div>
                                                                        </>
                                                                        :
                                                                        <></>
                                                            }
<div className="row">
															<div className="col-md-4">

                                                            <Link to={"/jobs-list/"+data._id} title="Jobs listing"><img src={require('../../imgs/view.jpg').default} width="22" alt="" /></Link>
															</div>
                                                            </div>
                                                            <div className="col-md-4">
                                                                <Link to="/#" ><img src={require('../../imgs/edit.jpg').default} alt="" width="22" /></Link>
                                                            </div>
                                                            <div className="col-md-4">
                                                                <Link to="/#"><img src={require('../../imgs/delete.jpg').default} width="22" alt="" /></Link>
                                                            </div>
                                                        </div>
                                                     */}
                                                    </td>
                                                </tr>
                                            })}


                                        </tbody>

                                    </table>
                                </div>
                            </div>


                        </div>

                    </div>
                    {/* <!-- End of Main Content --> */}

                    {/* <!-- Footer --> */}
                    <footer className="sticky-footer bg-white">
                        <div className="container my-auto">
                            <div className="copyright text-center my-auto">
                                <span>Copyright Job 2020</span>
                            </div>
                        </div>
                    </footer>
                    {/* <!-- End of Footer --> */}

                </div>
                {/* <!-- End of Content Wrapper --> */}

            </div>
        </div>
    );
}
export default UserList;
