import { useEffect, useState } from "react";
import { Link,useParams,useHistory } from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import Header from "./Header";
import useToken from "./useToken";






function JobCandidates(){
	const {id} = useParams();
	const history = useHistory();
	const token = useToken();
    if(!token.token) {
        history.push('/admin');
      }
	const [list,setList] = useState([]);

	useEffect(() => {
		getOrgJobCandidates();
    },[]);

	const getOrgJobCandidates = async ()=>{
		var data = new FormData();
        data.append('job_id', id);
     
        ApiFunctions.getJobCandidates(data,{
            headers:{
                'content-type':'multipart/form-data',
				'Authorization':token.token.token,
            }
        }).then(response => {
          
            setList(response.data.data)
        }).catch(e => {
            window.alert('Some error occured');
         
        })
	};

    return (
        <div className="App">
            <Header />
            <div className="container">
	<div className="form-block">
		<h1>Candidates</h1>

		<div className="table-responsive">
			<table className="table table-bordered">
                <thead>
				<tr>
					<th width="7%">Sr No</th>
					<th width="15%">Name</th>
					<th width="30%">Email</th>
					<th width="30%">Contact</th>
					<th width="35%">Resume</th>
					{/* <th width="13%">Action</th> */}
				</tr>
                </thead>
                <tbody>
			
                                {list.map((data,id)=>{
				return <tr key={id}>
					<td>{id +1}</td>
					<td>{data.user_id.first_name +' '+ data.user_id.last_name}</td>
					<td>{data.user_id.email}</td>
					<td>{data.user_id.contact_number}</td>
					<td><a href={'../../../assets/resume/'+data.user_id.resume}  download >Resume</a></td>
					
					{/* <td className="pt-4">
						<Link to={"/job-candidates/"+data._id} className="btn btn-sm btn-info">Candidates</Link>
						<Link to="" className="btn btn-sm btn-danger">Cancel</Link>
					</td> */}
				</tr>
								})}
			 </tbody>
			</table>
		</div>

	</div>
</div>
        </div>
    )
}

export default JobCandidates;
