import logo from '../imgs/logo-job.jpg';
import user from '../imgs/user.jpg';
import { Helmet } from 'react-helmet';
import {
    Link,
    Redirect
} from "react-router-dom";
import useToken from './admin/useToken';


function Header() {
    const token = useToken();

    const logout = () => {
        localStorage.clear();
        return <Redirect to='/admin' />;
    };

    return (
        <div className="App">
            {/* // <Router> */}
            <Helmet>
                <link rel="stylesheet" href={require('../css/main.css').default} />
            </Helmet>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container">
                    <Link className="navbar-brand" to="/#"><img src={logo} alt="" /></Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
                            <li className="nav-item">

                                <Link className="nav-link active" aria-current="page" to="/index">Home</Link>

                            </li>
                            <li className="nav-item">
                                <Link className="nav-link" to="/about">About Us</Link>
                            </li>
                            {/* <li className="nav-item">
                                <a className="nav-link" href="/#">Contact Us</a>
                            </li> */}
                            <li className="nav-item">

                                <Link className="nav-link" to="/browse-jobs">Browse Jobs</Link>

                            </li>

                            {token.token !== null && token.token !== false ?
                                <>

                                    <li className="nav-item dropdown">
                                        <a className="nav-link dropdown-toggle user-profile" href="/#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                            <div className="user-avatar status-online"><img src={user} alt="" /></div>
                                            {token.token.data.user_role === 'user' ?
                                                <div className="user-name">{token.token.data.first_name}</div> :
                                                <div className="user-name">{token.token.data.org_name}</div>
                                            }
                                        </a>
                                        <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                                            {/* <li><a className="dropdown-item" href="/#">My Profile</a></li> */}
                                            <li><a className="dropdown-item" href="/#" onClick={logout}>Logout</a></li>
                                        </ul>
                                    </li>
                                </>
                                :
                                <>
                                    <li className="nav-item">
                                        <Link className="nav-link" to="/admin"> Login</Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link className="nav-link" to="/register_company"> Register</Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link className="nav-link" to="/register_user"> Register as Job seeker</Link>
                                    </li>
                                </>

                            }
                        </ul>
                    </div>
                </div>
            </nav>

            {/* // </Router> */}
        </div>
    );
}

export default Header;
