import { Helmet } from 'react-helmet';
import logo from '../../imgs/logo-job.jpg';
import '../../css/admin.css'
import {
    Link,
    Redirect,
    useHistory
    // useRouteMatch,
    // useParams
} from "react-router-dom";
import useToken from './useToken';

function Header() {
    const token = useToken();
    const history = useHistory();

    if(!token.token) {
        history.push('/admin');
      }


    const logout = () => {
        localStorage.clear();
        return <Redirect to='/admin' />;
    };

    return (
        <div className="App">

            {/* <Helmet>
                <link rel="stylesheet" href={require('../../css/admin.css').default} />
            </Helmet> */}
            <header>
                <nav className="navbar navbar-expand-lg navbar-light">
                    <div className="container">
                        <Link className="navbar-brand" to="/job-list"><img src={logo} alt="" /> </Link>
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
                                <li className="nav-item"><Link className="nav-link active" to="/job-list">Job list</Link></li>
                                <li className="nav-item"><Link className="nav-link" to="/PostJob">Post Job</Link></li>
                                <li className="nav-item"><a className="nav-link" href="/#"  onClick={logout}>{token.token.data.org_name}, Logout</a></li>
                            </ul>
                        </div>
                    </div>
                </nav>
            </header>
        </div>
    );
}

export default Header;
