import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";
import Footer from "./Footer";
import moment from "moment";

import { Helmet } from "react-helmet";




import dr2 from '../images/home2.jpg';




const AddSchedule = () => {

    const history = useHistory();

    const token = useToken();
    
    if(!token.token){
        history.push('/');
    }

    const [prescription,setPrescription] = useState('');
    const [document,setDocument] = useState('');


    const scheufle = async (e) => {

        e.preventDefault();
        var data = new FormData();  
        data.append('prescription', prescription);
        data.append('document', document);
        
        ApiFunctions.addPreviousPrescriptions(data,{
            headers:{
                'Authorization': token.token.token,
                'content-type':'multipart/form-data',
               }
        }).then(response => {
            history.push('/my-prescriptions');
    
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
   
    return (
        <div className="">
            <Header />
            <Helmet>
				<link rel="stylesheet" href={require('../css/style.css').default} />
			</Helmet>


          
    <section class="home_1 ap_img2">
        <div class="container">
            <div class="heading">
                <h1>
                    Add Schedule</h1>
            </div>
        </div>
    </section>
    <section class="appointment">
        <div class="container">
            <div class="form_wrap">
                <h3>Add Schedule</h3>
                <div class="row">
                    <div class="col6">
                        <form>
                            <div class="input">
                                <label>Day</label>
                                <select>
                                    <option>Select Day</option>
                                    <option>Monday</option>
                                </select>
                            </div>

                            <div class="input">
                                <label>Timings</label>
                                <input type="text" placeholder="" value=""/>
                            </div>

                            
                            <div class="form_btn">
                                <button type="submit">Add Schedule</button>
                            </div>

                        </form>
                    </div>
                    <div class="col6">
                        <div class="image"><img src={dr2} alt="appointment" title="appointment" /></div>
                    </div>
                </div>
            </div>
        </div>
    </section>

    <Footer />
               
        </div>
    );
}

export default AddSchedule;
