import { useEffect, useState } from "react";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";
import Footer from "./Footer";
import useToken from "./admin/useToken";
import { useHistory } from "react-router";
import { useParams } from "react-router-dom";
import { Helmet } from "react-helmet";



import eduction from '../images/eduction.png';
import register from '../images/register.png';
import location from '../images/location.png';
import language from '../images/language.png';
import doctor1 from '../images/doctor1.jpg';




const DoctorDetail = () => {

    const history = useHistory();
    const { id } = useParams();
        const token = useToken();
    
        if(!token.token){
            history.push('/');
        }
        
            
        const [doctor_id] = useState(id);
        const [details,setDetails] = useState([]);
        useEffect(() => {
            var data = new FormData();
            data.append('doctor_id', doctor_id);
         
            ApiFunctions.getSingleDoctor(data,{
                headers:{
                    'Authorization': token.token.token,
                   }
            }).then(response => {
                setDetails(response.data.data[0])
            }).catch(e => {
                window.alert('Some error occured');
            })
        });


        useEffect(() => {
            var data = new FormData();
            data.append('doctor_id', doctor_id);
         
            ApiFunctions.getSingleDoctor(data,{
                headers:{
                    'Authorization': token.token.token,
                   }
            }).then(response => {
                setDetails(response.data.data[0])
            }).catch(e => {
                window.alert('Some error occured');
            })
        });

        


        // console.log(details._hospital_id.name);
    return (
        <div className="">
            <Header />
            <Helmet>
				<link rel="stylesheet" href={require('../css/style.css').default} />
			</Helmet>


            <section class="detail">
        <div class="container">
            <div class="doctor_detail">
                <div class="dflex">
                    <div class="col6">
                        <h3>{details.full_name}</h3>
                        <div class="doctor_info">
                            <h4>Experience - {details.experience} years</h4>
                        </div>
                        <div class="info_wrap">
                            <div class="education">
                                <div class="img">
                                    <img src={eduction} alt="image" title="image" />
                                </div>
                                <div class="info">
                                    <h5>Qualifications </h5>
                                    <span> {details.education}</span>
                                </div>
                            </div>
                            <div class="education">
                                <div class="img">
                                    <img src={register} alt="image" title="image" />
                                </div>
                                <div class="info">
                                    <h5>Registration Number </h5>
                                    <span>{details.contact_number}</span>
                                </div>
                            </div>
                            <div class="education">
                                <div class="img">
                                    <img src={location} alt="image" title="image" />
                                </div>
                                <div class="info">
                                    <h5>Hospital </h5>
                                    <span>{details._hospital_id.name}
                                        </span>
                                </div>
                            </div>
                            <div class="education">
                                <div class="img">
                                    <img src={language} alt="image" title="image" />
                                </div>
                                <div class="info">
                                    <h5>Language </h5>
                                    <span>English, French </span>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="col6">
                        <div class="colright">
                            <div class="image">
                                <img src={doctor1} alt="dr" title="dr"/>
                            </div>
                            <p>Book your appointment for tomorrow </p>
                            <div class="button_wrap">
                                <a href="#" class="btn">Book virtual meeting </a>
                                <a href="#" class="btn">Book personal appointment
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="detail_wrap">
                    <h4>About </h4>
                    <p>Dr. Fedrick Thomas is one of the best cardiologists at Max Care Hospital and has been in the field of cardiology for more than 10 years. He has been working with us for more than 5 years and is a gold medal winner.
                    </p>
                    <a href="#">Know more </a>
                    <div class="award">
                        <h4>Awards and achievement </h4>
                        <span>Best cardiologist 2020</span>
                        <span>Best achiever 2019</span>
                        <span>Gold medalist in medicine </span>
                    </div>
                    <div class="clinic_address">
                        <h4>Clinic Address</h4>
                        <span><a href="#">Greams Lane, 21, Greams Rd, Thousand LightsChennai</a></span>
                    </div>
                    <div class="timings">
                        <h4>Timings</h4>
                        <ul>
                            <li><span>Monday</span>
                                <span> 11 AM to 4 PM </span></li>
                            <li><span>Tuesday</span>
                                <span>11 AM to 4 PM</span></li>
                            <li><span>Wednesday</span>
                                <span>2 PM to 7 PM </span></li>
                            <li><span>Thursday</span>
                                <span>Off </span></li>
                            <li><span>Friday</span>
                                <span>11 AM to 4 PM </span></li>
                            <li><span>Saturday</span>
                                <span>2 PM to 7 PM</span></li>
