import './App.css';
import {
  // BrowserRouter as Router,
  Route,
  Switch,
  // Redirect
  // useRouteMatch,
  // useParams
} from "react-router-dom";
import BrowseCompanies from './components/BrowseCompanies';
import CompanyDetails from './components/CompanyDetails';
import JobDetails from './components/JobDetails';
import PostJob from './components/admin/PostJob';
import Home from './components/home';
import About from './components/About';
import Login from './components/admin/login';
import useToken from './components/admin/useToken';
import Register from './components/register';
import ApplyJob from './components/ApplyJob';
import BrowseJobs from './components/BrowseJobs';
import RegisterAsUser from './components/registerAsUser';
import JobList from './components/admin/jobList';
import JobCandidates from './components/admin/jobCandidates';



function App() {


  const { token, setToken } = useToken();
 
  return (
    <div className="App">
  
   {/* <Home /> */}
    <Switch>
    <Route path="/" exact>
            <Home />
        </Route>  
          <Route path="/PostJob">
              <PostJob />
          </Route>
          <Route path="/browse-companies">
            <BrowseCompanies />
          </Route>
          <Route path="/browse-jobs">
            <BrowseJobs />
          </Route>
          <Route path="/company-details/:id">
            <CompanyDetails />
        </Route>
            <Route path="/job-details/:job_id">
            <JobDetails />
        </Route>
           
            <Route path="/about">
            <About />
        </Route>
         <Route path="/admin">
            <Login setToken={setToken} />
        </Route>
         <Route path="/register_company">
            <Register />
        </Route>
         <Route path="/register_user">
            <RegisterAsUser />
        </Route>
         <Route path="/apply-job/:id">
            <ApplyJob />
        </Route>
         <Route path="/job-list">
            <JobList />
        </Route>
         <Route path="/job-candidates/:id">
            <JobCandidates />
        </Route>
        </Switch>

    </div>
  );
}

export default App;
