import { Component } from "react";
import {
    Link,
  } from "react-router-dom";
import ApiFunctions from "../ApiFunctions";
// import CompanyDetails from "./CompanyDetails";
import Header from "./Header";

export default class BrowseJobs extends Component {
    constructor(props){
        super(props)
        this.state = {
          data: []
        }
      }
    
      getData(){
        ApiFunctions.getAllJobs().then(res => {
          var data = res.data.data

            this.setState({data : data})
        })
      }
      componentDidMount(){
        this.getData()
      }

    render (){
        return(
        <div className="App">
            <Header />
            <div className="bg-main">
                <div className="container dashboard-headline">
                    <div className="row">
                        <div className="col-md-12">
                            <h3>Jobs Lists</h3>
                            <nav id="breadcrumb" aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">Job Lists</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="pd-main">
                <div className="container">
                    <div className="row">

                        {this.state.data.map(d => (

                         <div className="col-md-12 col-lg-6" key={d._id}>
                            <div className="company-wrapper-content company-wrapper">
                                <div className="company-wrapper-main">
                                    <div className="row">
                                        <div className="col-lg-8 col-md-8 col-sm-8 col-xs-12">
                                            <div className="company-img">
                                                <img src={require('../imgs/software.png').default} alt="post_img" />
                                            </div>
                                            <div className="company-right-content company-job-cont">
                                                <h4><Link to={"/job-details/"+d._id}>{d.job_title}</Link></h4>
                                                <p>{d.job_type}</p>
                                                <ul>
                                                    {/* <li><i className="las la-map-marker-alt"></i> 4044 McKinley Avenue</li> */}
                                                    <li><i className="las la-map-marker-alt"></i> {d.contact_number}</li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div className="col-lg-4 col-md-4 col-sm-4 col-xs-12">
                                            <div className="company-right-btn-content">
                                                <ul>
                                                    <li><Link to={"/job-details/"+d._id}  >15 ACTIVE JOBS</Link></li>
                                                </ul>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="company-keyqord-main">
                                    <ul>
                                        <li><i className="fa fa-tags"></i>Keywords :</li>
                                        <li>ui designer,</li>
                                        <li>developer,</li>
                                        <li>senior</li>
                                        <li>it company,</li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                        



                 
                        ))
                        } 
                     
                    </div>
                </div>
            </div>
        </div>
        )
    }
}

