import Api from "./Api";

class ApiFunctions {
  getAllOrg() {
    return Api.get("/org/list");
  }
  getSingleOrg(data,headers) {
    return Api.post("/org/list/detail",data,headers);
  }
   getAllJobs() {
    return Api.get("/job/posted/list");
  }
   getSingleJobs(data,headers) {
    return Api.post("/job/posted/list/detail",data,headers);
  }

  applyJob(data,headers) {
    return Api.post("/apply/job", data,headers);
  }
  
  getOrgJobs(data,headers) {
    return Api.post("/job/post/by/org", data,headers);
  }
  
  getAllAdminJobs(data,headers) {
    return Api.post("/job/post/to/admin", data,headers);
  }


 
   
  getJobCandidates(data,headers) {
    return Api.post("/job/post/by/org/detail", data,headers);
  }
  
  register(data,headers) {
    return Api.post("/register",data,headers);
  }
  
  login(data,headers) {
    return Api.post("/login", data,headers);
  }
 

  postJob(data,headers){
    return Api.post('/add/job',data,headers);
  }

  orgStatus(data,headers){
    return Api.post('/approve/org/status',data,headers);
  }
  orgChangeStatus(data,headers){
    return Api.post('/change/status',data,headers);
  }
  jobPostStatus(data,headers){
    return Api.post('/change/job/post/status',data,headers);
  }
  getUsers(data,headers){
    return Api.post('/users/list',data,headers);
  }
  editJobPost(data,headers){
    return Api.post('/job/posted/list/edit',data,headers);
  }


}

export default new ApiFunctions();
