import { useEffect, useState } from "react";
import { Link ,useHistory} from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import Header from "./Header";
import useToken from "./useToken";






function JobList(){
	const token = useToken();
	const history = useHistory();
    if(!token.token) {
        history.push('/admin');
      }
const [list,setList] = useState([]);	  
const [id] = token.token.data._id;

	useEffect(() => {
		getJobs();
    },[]);

	const getJobs=async ()=>{
		var data = new FormData();
        data.append('org_id', id);
     
        ApiFunctions.getOrgJobs(data,{
            headers:{
                'content-type':'multipart/form-data',
				'Authorization':token.token.token,
            }
        }).then(response => {
          
            setList(response.data.data)
        }).catch(e => {
            window.alert('Some error occured');
         
        })
	};

    return (
        <div className="App">
            <Header />
            <div className="container">
	<div className="form-block">
		<h1>Jobs</h1>

		<div className="table-responsive">
			<table className="table table-bordered">
                <thead>
				<tr>
					<th width="7%">Sr No</th>
					<th width="15%">Job Title</th>
					<th width="30%">Description</th>
					<th width="35%">Qualification</th>
					<th width="13%">Action</th>
				</tr>
                </thead>
                <tbody>
			
                                {list.map((data,id)=>{
				return <tr key={id}>
					<td>{id +1}</td>
					<td>{data.job_title}</td>
					<td>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</td>
					<td>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. </td>
					<td className="pt-4">
						<Link to={"/job-candidates/"+data._id} className="btn btn-sm btn-info">Candidates</Link>
						{/* <Link to="" className="btn btn-sm btn-danger">Cancel</Link> */}
					</td>
				</tr>
								})}
			 </tbody>
			</table>
		</div>

	</div>
</div>
        </div>
    )
}

export default JobList;
