// import '../../css/admin.css';
// import e from "cors";
import React, {useState} from "react";
// import { useHistory } from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import { Helmet } from "react-helmet";
import { useHistory } from "react-router-dom";
import useToken from "./useToken";

const Login = ({setToken}) =>{

	const history = useHistory();
		const token = useToken();
		if(token.token){
		
		if(token.token.data.user_role === 'user'){
			history.push('/');
		}
		if(token.token.data.user_role === 'org'){
			history.push('/job-list');
		}
		}

	// localStorage.setItem('currentUser', '');
const [email, setEmail] = useState('');
const [password,setPassword]  = useState('');
const loginUser  = async(e) => {
	e.preventDefault();
	var data = new FormData();
	data.append('email', email);
	data.append('password', password);

	ApiFunctions.login(data,{
		headers:{
			'Content-Type': 'application/form-data'
		}
	}).then(response => {
		localStorage.setItem('currentUser', JSON.stringify(response.data));
		setToken(response.data);
		if(response.data.data.user_role === 'user'){
			history.push('/');
		}
		else if(response.data.data.user_role === 'org'){
			history.push('/job-list');
		}

	}).catch(e => {
		console.log(e);
	})

}   

    return (
        <div className="App">
			<Helmet>
<link rel="stylesheet"  href={require('../../css/admin.css').default} />
</Helmet>
<div id="login-block">
	<form action="" method="post" className="login-block">
		<h3>Login to New Company</h3>
		<div className="input-group">
			<input type="text" name="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="form-control" required />
		</div>
		<div className="input-group">
			<input type="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="form-control" required />
		</div>
		<div className="input-group">
			<button type="submit" value="Log in" onClick={loginUser} className="btn btn-primary">Login</button>
		</div>
	</form>
</div>	
</div>	
    );
}

export default Login;
