import { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import Header from "./Header";
import Sidebar from "./Sidebar";
import useToken from "./useToken";

// import "../../css/admin_resp.css";



function JobList() {

	const token = useToken();
	const history = useHistory();
	if (!token.token) {
		history.push('/admin');
	}
	const [list, setList] = useState([]);
	const [id] = token.token.data._id;

	useEffect(() => {
		getJobs();
	}, []);

	const getJobs = async () => {
		var data = new FormData();
		data.append('org_id', id);

		ApiFunctions.getOrgJobs(data, {
			headers: {
				'content-type': 'multipart/form-data',
				'Authorization': token.token.token,
			}
		}).then(response => {

			setList(response.data.data)
		}).catch(e => {
			window.alert('Some error occured');

		})
	};


	const Publish = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('publish_status', 'publish');
        data.append('job_id', e.target.value);

        ApiFunctions.jobPostStatus(data, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');

        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }

	const UnPublish = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('publish_status', 'unpublish');
        data.append('job_id', e.target.value);

        ApiFunctions.jobPostStatus(data, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');

        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }

	return (
		<div className="App">

			<div id="wrapper">

				<Sidebar />

				{/* // <!-- Content Wrapper --> */}
				<div id="content-wrapper" className="d-flex flex-column">

					{/* <!-- Main Content --> */}
					<div id="content">
						<Header />

						<div className="container-fluid">

							{/* <!-- Page Heading --> */}
							<div className="d-sm-flex align-items-center justify-content-between mb-4">
								<h1 className="h3 mb-0 text-gray-800">Jobs</h1>
								<Link to="/PostJob" className="btn btn-warning text-right"> Add Job</Link>
								<nav aria-label="breadcrumb">
									<ol className="breadcrumb">
										<li className="breadcrumb-item"><a href="index.php">Home</a></li>
										<li className="breadcrumb-item active" aria-current="page">Jobs</li>
									</ol>
								</nav>
							</div>

							<div id="list-details">

								<div className="table-responsive">
									<table className="table table-hover">
										<thead>
											<tr>
												<th scope="col">Sr. No</th>
												<th scope="col">Job title</th>
												<th scope="col">Description</th>
												<th scope="col">Qualification</th>
												<th scope="col">Status</th>
												<th scope="col">Action</th>
											</tr>
										</thead>
										<tbody>
											{list.map((data, id) => {
												return <tr key={id}>
													<td>{id + 1}</td>
													<td>{data.job_title}</td>
													<td>{data.job_description.substr(0, 200) + "..."}</td>
													<td>{data.qualification}</td>
													<td>
													<div className="row">
                                                            {data.publish_status === "unpublish" ?
                                                                <>
                                                                    <div className="col-md-4">
                                                                        <button type="button" className="btn btn-success btn-sm" onClick={Publish} value={data._id} >Publish</button>

                                                                    </div>
                                                                </>
                                                                :
                                                                data.publish_status === "publish" ?
                                                                    <>
                                                                        <div className="col-md-4">
                                                                            <button type="button" className="btn btn-danger btn-sm" onClick={UnPublish} value={data._id} >Unpublish</button>

                                                                        </div>
                                                                    </> :
                                                                    <></>

                                                            }
                                                        </div>


														</td>
													<td>
														<div className="row">
															{/* <div className="col-md-4">

																<Link to={"/job-candidates/" + data._id} title="Candidates listing"><img src={require('../../imgs/view.jpg').default} width="22" alt="" /></Link>
															</div> */}
															<div className="col-md-4">
																<Link to={"/edit-job-details/"+data._id} ><img src={require('../../imgs/edit.jpg').default} alt="" width="22" /></Link>
															</div>
															{/* <div className="col-md-4">
																<Link to="/#"><img src={require('../../imgs/delete.jpg').default} width="22" alt="" /></Link>
															</div> */}
														</div>
													</td>
												</tr>
											})}


										</tbody>

									</table>
								</div>
							</div>


						</div>

					</div>
					{/* <!-- End of Main Content --> */}

					{/* <!-- Footer --> */}
					<footer className="sticky-footer bg-white">
						<div className="container my-auto">
							<div className="copyright text-center my-auto">
								<span>Copyright Job 2020</span>
							</div>
						</div>
					</footer>
					{/* <!-- End of Footer --> */}

				</div>
				{/* <!-- End of Content Wrapper --> */}

			</div>
		</div>
	)
}

export default JobList;
