import { useState } from "react";
import { useHistory } from "react-router";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";


const RegisterAsUser =() =>{
    const history = useHistory();
    const [first_name,setFirstName] = useState('');
    const [last_name,setLastName] = useState('');
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [contact_number,setContactNumber] = useState('');
    const [resume,setResume] = useState();


    const changeHandler = (event) => {

		setResume(event.target.files[0]);

	};

    const register = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('first_name', first_name);
        data.append('last_name', last_name);
        data.append('email', email);
        data.append('password', password);
        data.append('contact_number', contact_number);
        data.append('user_role', 'user');
        data.append('resume', resume);


        ApiFunctions.register(data,{
            headers:{
                'content-type':'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');
            history.push('/admin');
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
    return (
        <div className="App">
            <Header />
            <div className="bg-main">
                <div className="container dashboard-headline">
                    <div className="row">
                        <div className="col-md-12">
                            <h3>Register</h3>
                            <nav id="breadcrumb" aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">Register</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="pd-main">
                <div className="container">
                    <div className="profile-content-inner-area">
                        <form action="" method="post">
                            <div className="row justify-content-center">
                                <div className="col-xl-6">
                                    <div className="dashboard-box margin-top-0 margin-bottom-30">
                                        <div className="headline">
                                            <h3>Register</h3>
                                        </div>
                                        <div className="content with-padding padding-bottom-0">
                                            <div className="row">
                                                {/* <input type="hidden" name="org_id" value={org_id}  onChange={(e) => setOrgId(e.target.value)} /> */}
                                                <div className="col-xl-12 col-md-12 col-sm-12">
                                                    <div className="submit-field">
                                                        <h5 className="text-left">First Name</h5>
                                                        <input type="text" name="first_name" className="input-border" value={first_name}  onChange={(e) => setFirstName(e.target.value)} required />
                                                    </div>
                                                </div>  
                                                <div className="col-xl-12 col-md-12 col-sm-12">
                                                    <div className="submit-field">
                                                        <h5>Last Name</h5>
                                                        <input type="text" name="last_name" className="input-border" value={last_name}  onChange={(e) => setLastName(e.target.value)} required />
                                                    </div>
                                                </div>
                                                <div className="col-xl-12 col-md-12 col-sm-12">
                                                    <div className="submit-field">
                                                        <h5>Contact No</h5>
                                                        <input type="text" className="input-border" name="contact_no" value={contact_number}  onChange={(e) => setContactNumber(e.target.value)} required />
                                                    </div>
                                                </div>
                                               
                                                <div className="col-xl-12 col-md-12 col-sm-12">
                                                    <div className="submit-field">
                                                        <h5>Email</h5>
                                                        <input type="text" name="email" className="input-border" value={email}  onChange={(e) => setEmail(e.target.value)} required />
                                                    </div>
                                                </div>
                                                <div className="col-xl-12 col-md-12 col-sm-12">
                                                    <div className="submit-field">
                                                        <h5>Password</h5>
                                                        <input type="password" name="password" className="input-border" value={password}  onChange={(e) => setPassword(e.target.value)} required />
                                                    </div>
                                                </div>

                                                  <div className="col-xl-12 col-md-12 col-sm-12">
                                                    <div className="submit-field">
                                                        <h5>Upload Resume</h5>
                                                        <input type="file" name="upload_resume" onChange={changeHandler} />
                                                    </div>
                                                </div>
                                                
                                                {/* <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Full Name</h5>
                                                        <input type="text" className="input-border" name="name" value="Adam Dale" required />
                                                    </div>
                                                </div> */}
                                               
                                             
                                            </div>
                                            <button type="submit" className="" onClick={register}>Register</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default RegisterAsUser;
