import { useEffect, useState } from "react";
import { useHistory, useParams } from "react-router";
import ApiFunctions from "../ApiFunctions";
import useToken from "./admin/useToken";
import Header from "./Header";



function ApplyJob() {

const history = useHistory();
const { id } = useParams();
    const token = useToken();

    if(!token.token){
     history.push('/admin');
    }
    else if(token.token.data.user_role !== 'user'){
        alert('You are an organisation. So you cant apply to a job')
        history.push('/');
    }

    const [user_id] = useState(token.token.data._id);
    const [job_id] = useState(id);
    const [details,setDetails] = useState('');
    useEffect(() => {
        var data = new FormData();
        data.append('user_id', user_id);
        data.append('job_id', job_id);
     
        ApiFunctions.getSingleJobs(data,{
            headers:{
                'content-type':'multipart/form-data'
            }
        }).then(response => {
            // window.alert('Sucess');
            setDetails(response.data.data[0])
        }).catch(e => {
            window.alert('Some error occured');
         
        })
    },[job_id,user_id]);

    const applyJob = async (e)=>{
        e.preventDefault();
        var data = new FormData();
        data.append('job_id',job_id);
        data.append('user_id',user_id);

        ApiFunctions.applyJob(data,{
                headers:{
                    'Authorization':token.token.token,
                    'content-type':'multipart/form-data',
                }
            }).then(response => {
                window.alert('Sucess');
                history.push('/browse-jobs')
            }).catch(e => {
                window.alert('Some error occured');
                console.log(e);
            })

    }
      

    return (
        <div className="App">
            <Header />
            <div className="bg-main">
                <div className="container dashboard-headline">
                    <div className="row">
                        <div className="col-md-12">
                            <h3>Apply Job</h3>
                            <nav id="breadcrumb" aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">Apply Job</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="pd-main">
                <div className="container">
                    <div className="profile-content-inner-area">
                        <form action="" method="post">
                            <div className="row">
                                <div className="col-xl-12">
                                    <div className="dashboard-box margin-top-0 margin-bottom-30">
                                        <div className="headline">
                                            <h3>Job Apply Confirmation</h3>
                                        </div>
                                        <div className="content with-padding padding-bottom-0">
                                            <div className="row">
                                            <input type="hidden" name="job_id" value={id}   />
                                            <input type="hidden" name="user_id" value={user_id}   />

                                                <div className="col-xl-12 col-md-12 col-sm-12">
                                                    <div className="submit-field">
                                                        <h5>Job</h5>
                                                        <input type="text" className="input-border" name="name" value={details.job_title} required />
                                                    </div>
                                                </div>  
                                                {/* <div className="col-xl-6 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Last Name</h5>
                                                        <input type="text" className="input-border" name="name" value="Adam Dale" required />
                                                    </div>
                                                </div> */}
                                            
                                                {/* <div className="col-xl-12 col-md-6 col-sm-6">
                                                    <div className="submit-field">
                                                        <h5>Upload Resume</h5>
                                                        <input type="file" name="upload_resume" />
                                                    </div>
                                                </div> */}
                                            </div>
                                            <button type="submit" onClick={applyJob} className="">Apply Now</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ApplyJob;
