import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";
import Footer from "./Footer";
import moment from "moment";

import { Helmet } from "react-helmet";




import dr2 from '../images/home2.jpg';




const WritePrescription = () => {

    // const [jobList,setJobList] = useState([]);
    // // const [orgList,setOrgList] = useState([]);
    // moment.locale('en');
    // // let date = "";
    // useEffect(() => {
	// 	// ApiFunctions.getAllOrg().then(res => {
    //     //   var data = res.data.data

    //     //   setOrgList( data)
    //     // })


    //     ApiFunctions.getAllJobs().then(res => {
    //         var data = res.data.data;

             
    //           const list = data.map((d,i)=>{
    //           var date = d.createdDate;
    //           data[i].date = moment(date).startOf('seconds').fromNow()
    //           return  data[i];
    //           });

    //         setJobList(list)
    //       })

	// }, []);
   
    return (
        <div className="">
            <Header />
            <Helmet>
				<link rel="stylesheet" href={require('../css/style.css').default} />
			</Helmet>


          
    <section class="home_1 ap_img2">
        <div class="container">
            <div class="heading">
                <h1>
                Write Prescription</h1>
            </div>
        </div>
    </section>
    <section class="appointment">
        <div class="container">
            <div class="form_wrap">
                <h3>Write Prescription</h3>
                <div class="row">
                    <div class="col6">
                        <form>
                            <div class="input">
                                <label>Select Patient</label>
                                <select name="patient_id" value={day} className="form-control" onChange={(e) => setDay(e.target.value)} required>
                                    <option value="">Select Patient</option>
                                    <option value="Monday">Monday</option>
                                   
                                </select>
                            </div>


                            <div class="input">
                                <label>Prescription</label>
                                <input type="text" value={timing}  name="pres"   onChange={(e) => setTiming(e.target.value)} placeholder="" required/>
                            </div>



                            <div class="input">
                                <label>Prescription</label>
                                <textarea type="text" placeholder="" ></textarea>
                            </div>

                         
                            <div class="form_btn">
                                <button type="submit">Write Prescription</button>
                            </div>

                        </form>
                    </div>
                    <div class="col6">
                        <div class="image"><img src={dr2} alt="appointment" title="appointment" /></div>
                    </div>
                </div>
            </div>
        </div>
    </section>

    <Footer />
               
        </div>
    );
}

export default WritePrescription;
