import { Link } from "react-router-dom";
import logo from '../../imgs/logo-job.jpg';
import useScript from "./useScript";
import { Helmet } from "react-helmet";

import "../../css/ad-main.css";
import "../../css/admin.css";
import useToken from "./useToken";

function Sidebar() {
	useScript('js/jquery.min.js');
	useScript('js/admin/sb-admin-2.js');

    const token = useToken();
    // if (token.token) {

    //     if (token.token.data.user_role !== 'admin') {
    //         history.push('/admin');
    //     }
    // }
  


    return (
        <div className="App">
            		<Helmet>
				<link rel="stylesheet" media="screen and (max-width: 1200px)" href={require('../../css/admin_resp.css').default} />
				{/* <link rel="stylesheet" href={require('../../css/admin.css').default} /> */}
				<link rel="stylesheet" href={require('../vendor/fontawesome-free/css/all.min.css').default} />
			</Helmet>
            <ul className="navbar-nav sidebar-background sidebar sidebar-dark accordion" id="accordionSidebar">

                {/* <!-- Sidebar - Brand --> */}
                <a className="sidebar-brand d-flex align-items-center justify-content-center" href="index.html">
                    <img src={logo} alt="" />
                </a>

                {/* <!-- Divider --> */}
                <hr className="sidebar-divider my-0" />
          
                {/* <!-- Nav Item - Dashboard --> */}
               


          {token.token.data.user_role ==='admin' ?
        <>
         <li className="nav-item active">
         <Link className="nav-link" to="/organizations">
                        <i className="fas fa-fw fa-tachometer-alt"></i>
                        <span>Dashboard</span></Link>
                </li>

                {/* <!-- Divider --> */}
                <hr className="sidebar-divider" />
        <li className="nav-item">
            <Link className="nav-link" to="/organizations">
                <i className="fas fa-fw fa-folder"></i>
                <span>Oganizations</span>
            </Link>
        </li>
        <li className="nav-item">
            <Link className="nav-link" to="/user-list">
                <i className="fas fa-fw fa-folder"></i>
                <span>Users</span>
            </Link>
        </li>
        </>  :  token.token.data.user_role ==='org'?
        <>
         <li className="nav-item active">
         <Link className="nav-link" to="/job-list">
                        <i className="fas fa-fw fa-tachometer-alt"></i>
                        <span>Dashboard</span></Link>
                </li>

                {/* <!-- Divider --> */}
                <hr className="sidebar-divider" />
        <li className="nav-item">
            <Link className="nav-link" to="/job-list">
                <i className="fas fa-fw fa-folder"></i>
                <span>Posted Jobs</span>
            </Link>
        </li> 
        </>
        :
        <>
        </>
        }
                {/* <li className="nav-item">
                    <a className="nav-link" href="organisation.php">
                        <i className="fas fa-fw fa-folder"></i>
                        <span>Organisation</span>
                    </a>
                </li> */}

                {/* <!-- Nav Item - Charts --> */}
                {/* <li className="nav-item">
                    <a className="nav-link" href="reviews.php">
                        <i className="fas fa-fw fa-chart-area"></i>
                        <span>Reviews</span></a>
                </li> */}

                {/* <!-- Nav Item - Tables --> */}
                {/* <li className="nav-item">
                    <a className="nav-link" href="change-password.php">
                        <i className="fas fa-fw fa-table"></i>
                        <span>Change Password</span></a>
                </li> */}


            </ul>
        </div>
    );
}

export default Sidebar;
