import logo from '../images/logo.png';

// import user from '../imgs/user.jpg';
import { Helmet } from 'react-helmet';
import {
    useHistory,
    Link,
    Redirect
} from "react-router-dom";
import useToken from './admin/useToken';


function Header() {
    const token = useToken();
    const history = useHistory();

    if(!token.token) {
        history.push('/login');
    }

    const isLoggedIn = token.token;
    var user_role = "";
    if(isLoggedIn){
         user_role = token.token.data.user_role;
    }


    const logout = () => {
        localStorage.clear();
        return <Redirect to='/' />;
    };


    return (
        <div className="">
            <Helmet>
                <link rel="stylesheet" href={require('../css/main.css').default} />
            </Helmet>
            <header>
                <div class="container">
                    <nav class="navbar navbar-expand-lg">
                        <a class="navbar-brand" href="/">
                            <img src={logo}/>
                        </a>
                        <div class="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul class="navbar-nav">

                                <li class="nav-item">
                                    <a class="nav-link" href="/">Home</a>
                                </li>


                                {isLoggedIn ? 
                                    (
                                        <>  
                                        {user_role === 'patient' ? 
                                            <li class="nav-item">
                                                <a class="nav-link" href="/#/doctors">Doctors</a>
                                            </li>
                                        : ""
                                        }


                                        {user_role === 'patient' ? 
                                            <li class="nav-item">
                                                <a class="nav-link" href="/#/my-appointments">My Appointments</a>
                                            </li>
                                        : ""
                                        }

                                        

                                        


                                        <li class="nav-item">
                                        <a  class="nav-link" href="/#" onClick={logout} >
                                        {
                                            token.token.data.username.charAt(0).toUpperCase()+ token.token.data.username.slice(1)
                                        }
                                        ,Logout</a></li>

                                        </>

                                    ) : (
                                        <>    
                                            
                                            <li class="nav-item">
                                                <a class="nav-link" href="/#/register">Register</a>
                                            </li>
                                            <li class="nav-item">
                                                <a class="nav-link" href="/#/login">Login</a>
                                            </li>
                                        </>
                                    )
                                }
                            
                               

                                {/* <li class="nav-item">
                                    <a class="nav-link" href="/">Home</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="/#/register">Register</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="/#/login">Login</a>
                                </li> */}
                            </ul>
                        </div>
                    </nav>
                </div>
            </header>
        </div>
    );
}

export default Header;
