import { useState } from "react";
import { useHistory } from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import useToken from "./useToken";
import Header from "./Header";
import Sidebar from "./Sidebar";
// import Home from "./home";



const PostJob = () => {
    // const orgIdParam = useParams();
    const history = useHistory();
    const [job_title, setJobTitle] = useState('');
    const [job_description, setJobDescription] = useState('');
    const [job_type, setJobType] = useState('');
    const [qualification, setQualification] = useState('');
    const [contact_number, setContactNumber] = useState('');
    const [email, setEmail] = useState('');
    const [salary, setSalary] = useState('');
    const [experience, setExperience] = useState('');

    // const token = getToken(history);
    const token = useToken();
    if (!token.token) {
        history.push('/admin');
    }


    function handleSelectChange(event) {

        setJobType(event.target.value)

    }
    function handleQualificationChange(event) {
        setQualification(event.target.value)

    }


    const postJob = async (e) => {
        e.preventDefault();
        var data = new FormData();
        data.append('org_id', org_id);
        data.append('job_title', job_title);
        data.append('job_description', job_description);
        data.append('job_type', job_type);
        data.append('qualification', qualification);
        data.append('contact_number', contact_number);
        data.append('email', email);
        data.append('experience', experience);
console.log(data)
ApiFunctions.postJob(data, {
            headers: {
                'Authorization': token.token.token,
            }
        }).then(response => {
            window.alert('Sucess');
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }

    const [org_id, setOrgId] = useState(token.token.data._id);

    return (
        <div className="App">
            <div id="wrapper">

                <Sidebar />

                {/* // <!-- Content Wrapper --> */}
                <div id="content-wrapper" className="d-flex flex-column">

                    {/* <!-- Main Content --> */}
                    <div id="content">
                        <Header />


                        <div className="container-fluid">

                            {/* <!-- Page Heading --> */}
                            <div className="d-sm-flex align-items-center justify-content-between mb-4">
                                <h1 className="h3 mb-0 text-gray-800">Add Job</h1>
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb">
                                        <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                        <li className="breadcrumb-item active" aria-current="page">Add Job</li>
                                    </ol>
                                </nav>
                            </div>

                            <div id="addJob">
                                <form>
                                    <div className="addHotel_inner">
                                        <div className="row">
                                            <div className="col-md-12 col-lg-6">
                                                <div className="form-group">
                                                    <input type="hidden" name="org_id" value={org_id} onChange={(e) => setOrgId(e.target.value)} />
                                                    <label htmlFor="job_title">Job Title</label>
                                                    <input type="text" name="job_title" placeholder="Job Title" className="form-control" value={job_title} onChange={(e) => setJobTitle(e.target.value)} required />
                                                </div>
                                                <div className="form-group">
                                                    <label htmlFor="email">Email</label>
                                                    <input type="text" className="form-control" placeholder="Email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                                                </div>
                                            </div>
                                            <div className="col-md-12 col-lg-6">
                                                <div className="form-group">
                                                    <label htmlFor="job_type">Job Type</label>
                                                    <select name="job_type" value={job_type} className="form-control" onChange={handleSelectChange} required>
                                                        <option value="">---Select Job Type---</option>
                                                        <option value="IT">IT</option>
                                                        <option value="Management">Management</option>
                                                    </select>
                                                </div>
                                                <div className="form-group">
                                                    <label htmlFor="qualification">Qualification</label>
                                                    <select name="qualification" value={qualification} className="form-control" onChange={handleQualificationChange} required>
                                                        <option value="">---Select Qualification---</option>
                                                        <option value="MCA">MCA</option>
                                                        <option value="MBA">MBA</option>
                                                        <option value="BCA">BCA</option>
                                                        <option value="BBA">BBA</option>
                                                    </select>
                                                </div>
                                            </div>
                                            <div className="col-md-12 col-lg-6">
                                                <div className="form-group">
                                                    <label htmlFor="salary">Salary</label>
                                                    <input type="number" name="salary" placeholder="Salary" className="form-control" value={salary} onChange={(e) => setSalary(e.target.value)} required />
                                                </div>

                                            </div>

                                            <div className="col-md-12 col-lg-6">
                                                <div className="form-group">
                                                    <label htmlFor="email">Experience</label>
                                                    <input type="number" className="form-control" placeholder="Experience" name="experience" value={experience} onChange={(e) => setExperience(e.target.value)} required />
                                                </div>
                                            </div>

                                        </div>
                                        <div className="row">
                                            <div className="col-md-12">
                                                <div className="form-group">
                                                    <label htmlFor="description">Description</label>
                                                    <textarea name="job_desc" className="form-control" rows="3" placeholder="Explain in detail about desired job" value={job_description} onChange={(e) => setJobDescription(e.target.value)} required></textarea>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="row">
                                            <div className="col-md-12">
                                                <div className="form-group">
                                                    <button type="submit" className="btn btn-warning" onClick={postJob}>Apply Now</button>
                                                    <button type="submit" className="btn btn-secondary">Cancel</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </div>


                        </div>



                    </div>
                    {/* <!-- End of Main Content --> */}

                    {/* <!-- Footer --> */}
                    <footer className="sticky-footer bg-white">
                        <div className="container my-auto">
                            <div className="copyright text-center my-auto">
                                <span>Copyright Job 2020</span>
                            </div>
                        </div>
                    </footer>
                    {/* <!-- End of Footer --> */}

                </div>
                {/* <!-- End of Content Wrapper --> */}

            </div>



            {/* <div className="container">
                <div className="form-block">
                    <h1>Post Job</h1>
                    <form action="" method="post">
                        <div className="row">




                            <input type="hidden" name="org_id" value={org_id} onChange={(e) => setOrgId(e.target.value)} />
                            <div className="col-md-12 col-md-12 ">
                                <div className="input-group">

                                    <input type="text" name="job_title" placeholder="Job Title" className="form-control" value={job_title} onChange={(e) => setJobTitle(e.target.value)} required />
                                </div>
                                <div className="input-group">
                                    <input type="text" className="form-control" placeholder="Email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                                </div>
                            </div>
                            <div className="col-md-12 col-md-12 ">
                                <div className="input-group">
                                    <textarea name="job_desc" className="form-control" rows="3" placeholder="Explain in detail about desired job" value={job_description} onChange={(e) => setJobDescription(e.target.value)} required></textarea>
                                </div>
                            </div>
                            


                            <div className="col-xl-6 col-md-6 col-sm-6">
                                <div className="input-group">
                                    <select name="job_type" value={job_type} className="form-control" onChange={handleSelectChange} required>
                                        <option value="">---Select Job Type---</option>
                                        <option value="IT">IT</option>
                                        <option value="Management">Management</option>
                                    </select>
                                </div>
                            </div>
                           
                            <div className="col-xl-6 col-md-6 col-sm-6">
                                <div className="input-group">
                                    <select name="qualification" value={qualification} className="form-control" onChange={handleQualificationChange} required>
                                        <option value="">---Select Qualification---</option>
                                        <option value="MCA">MCA</option>
                                        <option value="MBA">MBA</option>
                                        <option value="BCA">BCA</option>
                                        <option value="BBA">BBA</option>
                                    </select>
                                </div>
                            </div>

                            <button type="submit" className="btn btn-primary" onClick={postJob}>Apply Now</button>



                        </div>
                    </form>

                </div>
            </div> */}
        </div>
    );
}

export default PostJob;
