// import { Helmet } from 'react-helmet';

// import user from '../../imgs/user_icon.png';
import '../../css/admin.css'
import {
    Redirect,
    useHistory
    // useRouteMatch,
    // useParams
} from "react-router-dom";
import useToken from './useToken';

function Header() {
    const token = useToken();
    const history = useHistory();
    if(!token.token) {
        history.push('/admin');
    }

    const isLoggedIn = token.token;

    const logout = () => {
        localStorage.clear();
        return <Redirect to='/admin' />;
    };

    return (
        <div className="">    
            <link rel="stylesheet" href={require('../../css/admin.css').default} />   
            <link rel="stylesheet" href={require('../../../node_modules/bootstrap/dist/css/bootstrap.css').default} />            
                <section class="page_wrapper page_wrapper_login">
                    
                </section>
            </div>
    );
}

export default Header;
