import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";
import Footer from "./Footer";
import useToken from "./admin/useToken";
import { useHistory } from "react-router";

import { Helmet } from "react-helmet";

import dr2 from '../images/home2.jpg';




const AddSchedule = () => {

    const history = useHistory();

    const token = useToken();
    
    if(!token.token){
        history.push('/');
    }

    const [timing,setTiming] = useState('');
    const [document,setDay] = useState('');


    const schedule = async (e) => {

        e.preventDefault();
        var data = new FormData();  
        data.append('prescription', prescription);
        data.append('document', document);
        
        ApiFunctions.addSchedule(data,{
            headers:{
                'Authorization': token.token.token,
               }
        }).then(response => {
            history.push('/schedule');
    
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
   
    return (
        <div className="">
            <Header />
            <Helmet>
				<link rel="stylesheet" href={require('../css/style.css').default} />
			</Helmet>


          
    <section class="home_1 ap_img2">
        <div class="container">
            <div class="heading">
                <h1>
                    Add Schedule</h1>
            </div>
        </div>
    </section>
    <section class="appointment">
        <div class="container">
            <div class="form_wrap">
                <h3>Add Schedule</h3>
                <div class="row">
                    <div class="col6">
                        <form  action="" method="post">
                            <div class="input">
                                <label>Day</label>
                                <select name="day" value={day} className="form-control" onChange={(e) => setDay(e.target.value)}>
                                    <option>Select Day</option>
                                    <option>Monday</option>
                                </select>
                            </div>

                            <div class="input">
                                <label>Timings</label>
                                <input type="text" value={timing}  name="timing"   onChange={(e) => setTiming(e.target.value)} placeholder="" value=""/>
                            </div>

                            
                            <div class="form_btn">
                                <button type="submit">Add Schedule</button>
                            </div>

                        </form>
                    </div>
                    <div class="col6">
                        <div class="image"><img src={dr2} alt="appointment" title="appointment" /></div>
                    </div>
                </div>
            </div>
        </div>
    </section>

    <Footer />
               
        </div>
    );
}

export default AddSchedule;
