import moment from "moment";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";
import useToken from "./admin/useToken";

function JobDetails() {
    const { job_id } = useParams();
    const token = useToken();
// console.log(token.token);
    const [details, setDetails] = useState([]);

    useEffect(() => {
        var data = new FormData();
        data.append('job_id', job_id);

        ApiFunctions.getSingleJobs(data, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }).then(response => {
            setDetails(response.data.data[0])
        }).catch(e => {
            window.alert('Some error occured');

        })
    }, [job_id]);

    moment.locale('en');
    var dt = details.createdDate;
    var date = moment(dt).format('DD-MMM-Y HH:mm:ss')
    return (
        <div className="App">
            <Header />
            <div className="bg-main">
                <div className="container dashboard-headline">
                    <div className="row">
                        <div className="col-md-12">
                            <h3>{details.job_title}</h3>
                            <nav id="breadcrumb" aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">{details.job_title}</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="pd-main">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-4">
                            <div className="job-sidebar">
                                <h4 className="job-sidebar-title">Job Overview</h4>
                                <div className="widget-content">
                                    <ul className="job-details">
                                        <li>
                                            <i className="las icon la-calendar"></i>
                                            <h5>Date Posted:</h5>
                                            <span>{date}</span>
                                        </li>
                                        <li>
                                            <i className="las icon la-calendar-check"></i>
                                            <h5>Company:</h5>
                                            <span>{details?._org_id?.org_name}</span>
                                        </li>
                                        <li>
                                            <i className="las icon la-map-marker"></i>
                                            <h5>Location:</h5>
                                            <span>London, UK</span>
                                        </li>
                                        <li>
                                            <i className="las icon la-wallet"></i>
                                            <h5>Salary:</h5>
                                            <span>$35k - $45k</span>
                                        </li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                        <div className="col-lg-8">
                            <h4>Job Description</h4>
                            <p>{details.job_description}</p>

                            <h4 className="mt-3">Skill & Experience</h4>
                            <ul>
                                <li><i className="las la-caret-right"></i> {details.experience}</li>
                                <li><i className="las la-caret-right"></i> {details.qualification}</li>
                            </ul>

                            <div className="apply-btn">
                                {token.token === undefined || token.token === null ? <Link to={"/admin"} className="apply">Login to Apply</Link> :<Link to={"/apply-job/" + details._id} className="apply">Apply Now</Link>}
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    );
}

export default JobDetails;
