// import useScript from "./useScript";
import { Helmet } from "react-helmet";

import "../../css/ad-main.css";
import "../../css/admin.css";
import useToken from "./useToken";
import {
    Link,
    Redirect
} from "react-router-dom";


function Sidebar() {
	// useScript('js/jquery.min.js');

    const token = useToken();
    const logout = () => {
        localStorage.clear();
        return <Redirect to='/admin' />;
    };

  


    return (
        <div className="App">
            <Helmet>
				<link rel="stylesheet" media="screen and (max-width: 1200px)" href={require('../../css/admin.css').default} />
				{/* <link rel="stylesheet" href={require('../../css/admin.css').default} /> */}
				<link rel="stylesheet" href={require('../vendor/fontawesome-free/css/all.min.css').default} />
			</Helmet>


            <div class="main_sidebar" className="App" >
                <div class="logo">
                    <h3>Cura Admin</h3>
                </div>
                <ul class="main_sidemenu">
                <li><a href="dashboard.php"><i class="fa fa-home" aria-hidden="true"></i><span>Dashboard</span></a></li>
                <li><a href="stage-list.php"><i class="fa fa-file" aria-hidden="true"></i><span>Stages</span></a></li>
                <li><a href="test-list.php"><i class="fa fa-pencil" aria-hidden="true"></i><span>Tests</span></a></li>
                <li><a href="/#" onClick={logout}><i class="fa fa-pencil" aria-hidden="true"></i><span>Logout</span></a></li>
                </ul>
            </div>
        </div>
    );
}

export default Sidebar;
