import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ApiFunctions from "../ApiFunctions";
import Header from "./Header";
import Footer from "./Footer";
import moment from "moment";

import { Helmet } from "react-helmet";




import dr2 from '../images/home2.jpg';




const BookAppointment = () => {


    const history = useHistory();
    const [first_name,setFirstName] = useState('');
    const [last_name,setLastName] = useState('');
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [contact_number,setContact] = useState('');
    const [username,setUsername] = useState('');
    const [age,setAge] = useState('');
    const [gender,setGender] = useState('');
    const [address,setAddress] = useState('');

    const appointment = async (e) => {


        e.preventDefault();
        var data = new FormData();
        data.append('name', name);
        data.append('email', email);
        data.append('contact', contact);
        
        data.append('doctor_id', doctor_id);
        data.append('appointment_date', appointment_date);
        data.append('appointment_time', appointment_time);
      

        ApiFunctions.bookAppointment(data,{
            headers:{
                'content-type':'multipart/form-data'
            }
        }).then(response => {
            // window.alert('Success');
            history.push('/my-appointments');
    
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    }
   
    return (
        <div className="">
            <Header />
            <Helmet>
				<link rel="stylesheet" href={require('../css/style.css').default} />
			</Helmet>


          
    <section class="home_1 ap_img2">
        <div class="container">
            <div class="heading">
                <h1>Book Appointment</h1>
            </div>
        </div>
    </section>
    <section class="appointment">
        <div class="container">
            <div class="form_wrap">
                <h3>Book Appointment</h3>
                <div class="row">
                    <div class="col6">
                        <form>
                            <div class="input">
                                <label>First Name</label>
                                <input type="text" value={name} onChange={(e) => setFirstName(e.target.value)} placeholder="" value="" />
                            </div>

                            <div class="input">
                                <label>Contact Number</label>
                                <input type="text" value={email} onChange={(e) => setFirstName(e.target.value)} placeholder="" value=""/>
                            </div>

                            <div class="input">
                                <label>Email</label>
                                <input type="text" value={contact} onChange={(e) => setFirstName(e.target.value)} placeholder="" value=""/>
                            </div>

                            <div class="input">
                                <label>Select Doctor</label>
                                <select >
                                    <option>Select Doctor</option>
                                </select>
                            </div>

                            <div class="input">
                                <label>Appointment Date</label>
                                <input type="date" value={appointment_date} onChange={(e) => setFirstName(e.target.value)} placeholder="" value=""/>
                            </div>

                            <div class="input">
                                <label>Appointment Time</label>
                                <input type="time" value={appointment_time} onChange={(e) => setFirstName(e.target.value)}  placeholder="" value=""/>
                            </div>

                         
                            <div class="form_btn">
                                <button type="submit" onclick={register1}>Book Appointment</button>
                            </div>

                        </form>
                    </div>
                    <div class="col6">
                        <div class="image"><img src={dr2} alt="appointment" title="appointment" /></div>
                    </div>
                </div>
            </div>
        </div>
    </section>

    <Footer />
               
        </div>
    );
}

export default BookAppointment;
