import Api from "./Api";

class ApiFunctions {
  
  register(data,headers) {
    return Api.post("/register/patient",data,headers);
  }
  
  login(data,headers) {
    return Api.post("/login", data,headers);
  }

  getHospitals(data,headers){
    return Api.post('/get/hospitals',data,headers);
  }

}

export default new ApiFunctions();
