import Api from "./Api";

class ApiFunctions {
  getAllOrg() {
    return Api.post("/org/list");
  }
  getSingleOrg(data,headers) {
    return Api.post("/org/list/detail",data,headers);
  }
   getAllJobs() {
    return Api.get("/job/posted/list");
  }

  get(id) {
    return Api.get(`/tutorials/${id}`);
  }

  register(data) {
    return Api.post("/register", data);
  }
  login(data,headers) {
    return Api.post("/login", data,headers);
  }

  postJob(data,headers){
    return Api.post('/add/job',data,headers);
  }

  update(id, data) {
    return Api.put(`/tutorials/${id}`, data);
  }

  delete(id) {
    return Api.delete(`/tutorials/${id}`);
  }

  deleteAll() {
    return Api.delete(`/tutorials`);
  }

  findByTitle(title) {
    return Api.get(`/tutorials?title=${title}`);
  }
}

export default new ApiFunctions();
