import './App.css';
import {
  // BrowserRouter as Router,
  Route,
  Switch,
  // Redirect
  // useRouteMatch,
  // useParams
} from "react-router-dom";
import BrowseCompanies from './components/BrowseCompanies';
import CompanyDetails from './components/CompanyDetails';
import JobDetails from './components/JobDetails';
import PostJob from './components/admin/PostJob';
import Home from './components/home';
import About from './components/About';
import Login from './components/admin/login';
import useToken from './components/admin/useToken';
import Register from './components/register';
import ApplyJob from './components/ApplyJob';
import BrowseJobs from './components/BrowseJobs';
import RegisterAsUser from './components/registerAsUser';
import JobList from './components/admin/jobList';
import JobCandidates from './components/admin/jobCandidates';
import Organizations from './components/admin/Organizations';
import EditPostJob from './components/admin/EditJobPost';
import AdminJobList from './components/admin/AdminJobList';
import UserList from './components/admin/UserList';



function App() {


  const { token, setToken } = useToken();

  return (
    <div className="App">

      <Switch>
        <Route path="/" exact>
          <Home />
        </Route>
        <Route path="/PostJob" exact>
          <PostJob />
        </Route>
        <Route path="/browse-companies" exact>
          <BrowseCompanies />
        </Route>
        <Route path="/browse-jobs" exact>
          <BrowseJobs />
        </Route>
        <Route path="/company-details/:id" exact>
          <CompanyDetails />
        </Route>
        <Route path="/job-details/:job_id" exact>
          <JobDetails />
        </Route>

        <Route path="/about" exact>
          <About />
        </Route>
        <Route path="/admin" exact>
          <Login setToken={setToken} />
        </Route>
        <Route path="/signup_company">
          <Register />
        </Route>
        <Route path="/signup">
          <RegisterAsUser />
        </Route>
        <Route path="/apply-job/:id">
          <ApplyJob />
        </Route>
        <Route path="/job-list">
          <JobList />
        </Route>
        <Route path="/organizations">
          <Organizations />
        </Route>
        <Route path="/job-candidates/:id">
          <JobCandidates />
        </Route>
        <Route path="/edit-job-details/:job_id" exact>
          <EditPostJob />
        </Route>
        <Route path="/jobs-list/:org_id" exact>
          <AdminJobList />
        </Route>
        <Route path="/user-list" exact>
          <UserList />
        </Route>
      </Switch>

    </div>
  );
}

export default App;
