import { NavLink } from "react-router-dom";
import Header from "./Header";

import { useParams } from "react-router-dom";
import ApiFunctions from "../ApiFunctions";
import { useEffect, useState } from "react";





function CompanyDetails() {


    
    const { id } = useParams();
    console.log(id);
    
    const [details,setDetails] = useState([]);
    
    // const orgDetail = async () => {  
     
    //     var data = new FormData();
    //     data.append('org_id', id);
     
    //     ApiFunctions.getSingleOrg(data,{
    //         headers:{
    //             'content-type':'multipart/form-data'
    //         }
    //     }).then(response => {
    //         window.alert('Sucess');
    //         setDetails(response)
    //         console.log(response);
    //     }).catch(e => {
    //         window.alert('Some error occured');
    //         console.log(e);
    //     })
    // }

    useEffect(() => {
        var data = new FormData();
        data.append('org_id', id);
     
        ApiFunctions.getSingleOrg(data,{
            headers:{
                'content-type':'multipart/form-data'
            }
        }).then(response => {
            window.alert('Sucess');
            setDetails(response.data.data[0])
        }).catch(e => {
            window.alert('Some error occured');
            console.log(e);
        })
    },[]);

    // orgDetail();
    
    console.log(details);
    // console.log(details);
    return (
        <div className="App">
            <Header />
            <div className="bg-main">
                <div className="container dashboard-headline">
                    <div className="row">
                        <div className="col-md-12">
                            <h3>{details.org_name}</h3>
                            <nav id="breadcrumb" aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="index.php">Home</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">Company Detail</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="pd-main">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-8">
                            <h4>Company Description</h4>
                            <p>Elementum eu facilisis sed odio morbi. Non quam lacus suspendisse faucibus interdum posuere lorem ipsum. Cursus metus aliquam eleifend mi in nulla posuere. Cras tincidunt lobortis feugiat vivamus at augue. Bibendum arcu vitae elementum curabitur vitae nunc sed. Leo vel fringilla est ullamcorper eget nulla. Curabitur vitae nunc sed velit dignissim sodales. Tellus in hac habitasse platea. Iaculis urna id volutpat lacus laoreet non curabitur gravida arcu. Eu scelerisque felis imperdiet proin fermentum leo vel. Morbi tincidunt ornare massa eget egestas purus. Et molestie ac feugiat sed lectus vestibulum.</p>

                            <p>Aliquam sem et tortor consequat id. Id venenatis a condimentum vitae sapien pellentesque habitant morbi tristique. Turpis egestas sed tempus urna et pharetra pharetra massa. Nibh sit amet commodo nulla facilisi nullam vehicula ipsum. Etiam non quam lacus suspendisse faucibus interdum posuere lorem. Etiam sit amet nisl purus in mollis nunc sed id. Urna et pharetra pharetra massa. A diam sollicitudin tempor id eu nisl nunc mi. Rhoncus dolor purus non enim praesent elementum facilisis leo vel. Ullamcorper sit amet risus nullam eget felis eget nunc. Sed cras ornare arcu dui vivamus arcu felis bibendum ut. Sagittis aliquam malesuada bibendum arcu vitae. Vulputate ut pharetra sit amet aliquam. Pellentesque pulvinar pellentesque habitant morbi tristique. Sit amet massa vitae tortor condimentum lacinia quis vel eros. Tortor posuere ac ut consequat semper viverra nam libero justo.</p>

                            <div className="dashboard-box margin-top-0 margin-bottom-30 job-list">
                                <h4>Current Opening</h4>
                                {/* <Router> */}
                                {/* <a href="/job-detail" className="job-listing"> */}
                                    <NavLink className="job-listing" to="/job-detail">
                                    <div className="job-listing-details">
                                        <div className="job-listing-description">
                                            <span className="dashboard-status-button job-status-item green"><i className="las la-building"></i> Full Time</span>
                                            <h3 className="job-listing-title">Web Designer, Graphic Designer, UI/UX Designer &amp; Art</h3>
                                            <div className="job-listing-footer">
                                                <ul>
                                                    <li><i className="las la-briefcase red-text"></i> Software Developer</li>
                                                    <li><i className="las la-wallet red-text"></i> $25000 - $32000</li>
                                                    <li><i className="las la-map-marker red-text"></i> 3832 Skinner Hollow Road</li>
                                                    <li><i className="las la-business-time red-text"></i> 15 Minute Ago</li>
                                                </ul>
                                            </div>
                                        </div>
                                    </div>
                                </NavLink>
                                {/* </a> */}
                                <NavLink className="job-listing" to="/job-detail">
                                    <div className="job-listing-details">
                                        <div className="job-listing-description">
                                            <span className="dashboard-status-button job-status-item green"><i className="las la-building"></i> Full Time</span>
                                            <h3 className="job-listing-title">Web Designer, Graphic Designer, UI/UX Designer &amp; Art</h3>
                                            <div className="job-listing-footer">
                                                <ul>
                                                    <li><i className="las la-briefcase red-text"></i> Software Developer</li>
                                                    <li><i className="las la-wallet red-text"></i> $25000 - $32000</li>
                                                    <li><i className="las la-map-marker red-text"></i> 3832 Skinner Hollow Road</li>
                                                    <li><i className="las la-business-time red-text"></i> 15 Minute Ago</li>
                                                </ul>
                                            </div>
                                        </div>
                                    </div>
                                </NavLink>

                                {/* </Router> */}
                            </div>

                        </div>
                        <div className="col-lg-4">
                            <div className="job-sidebar">
                                <h4 className="job-sidebar-title">Company Overview</h4>
                                <div className="widget-content">
                                    <ul className="job-details">
                                        <li>
                                            <i className="las icon la-calendar"></i>
                                            <h5>Established</h5>
                                            <span>August 11 2005</span>
                                        </li>
                                        <li>
                                            <i className="las icon la-calendar-check"></i>
                                            <h5>Employees</h5>
                                            <span>100-150</span>
                                        </li>
                                        <li>
                                            <i className="las icon la-map-marker"></i>
                                            <h5>Location:</h5>
                                            <span>London, UK</span>
                                        </li>
                                    </ul>
                                </div>

                                <h4 className="job-sidebar-title">Working Days</h4>
                                <div className="widget-content">
                                    <ul className="company-overview">
                                        <li>Monday<span>9AM - 5PM</span></li>
                                        <li>Tuesday<span>9AM - 5PM</span></li>
                                        <li>Wednesday<span>9AM - 5PM</span></li>
                                        <li className="active">Thursday<span>9AM - 5PM</span></li>
                                        <li>Friday<span>9AM - 5PM</span></li>
                                        <li>Saturday<span>9AM - 5PM</span></li>
                                        <li className="close-day">Sunday<span>Close</span></li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CompanyDetails
