import { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import ApiFunctions from "../../ApiFunctions";
import Header from "./Header";
import Sidebar from "./Sidebar";
import useToken from "./useToken";






function JobCandidates() {
	const { id } = useParams();
	const history = useHistory();
	const token = useToken();
	if (!token.token) {
		history.push('/admin');
	}
	const [list, setList] = useState([]);

	useEffect(() => {
		getOrgJobCandidates();
	}, []);

	const getOrgJobCandidates = async () => {
		var data = new FormData();
		data.append('job_id', id);

		ApiFunctions.getJobCandidates(data, {
			headers: {
				'content-type': 'multipart/form-data',
				'Authorization': token.token.token,
			}
		}).then(response => {

			setList(response.data.data)
		}).catch(e => {
			window.alert('Some error occured');

		})
	};

	return (
		<div className="App">

			<div id="wrapper">

				<Sidebar />

				{/* // <!-- Content Wrapper --> */}
				<div id="content-wrapper" className="d-flex flex-column">

					{/* <!-- Main Content --> */}
					<div id="content">
						<Header />
						<div className="container-fluid">

							{/* <!-- Page Heading --> */}
							<div className="d-sm-flex align-items-center justify-content-between mb-4">
								<h1 className="h3 mb-0 text-gray-800">Dashboard</h1>
								<nav aria-label="breadcrumb">
									<ol className="breadcrumb">
										<li className="breadcrumb-item"><a href="index.php">Home</a></li>
										<li className="breadcrumb-item active" aria-current="page">Form 2</li>
									</ol>
								</nav>
							</div>

							<div id="list-details">

								<div className="table-responsive">
									<table className="table table-hover">
										<thead>
											<tr>
												<th scope="col">Sr No</th>
												<th scope="col">Name</th>
												<th scope="col">Email</th>
												<th scope="col">Contact</th>
												<th scope="col">Resume</th>
												{/* <th scope="col">Action</th> */}

											</tr>
										</thead>
										<tbody>

											{list.map((data, id) => {
												return <tr key={id}>
													<td>{id + 1}</td>
													<td>{data.user_id.first_name + ' ' + data.user_id.last_name}</td>
													<td>{data.user_id.email}</td>
													<td>{data.user_id.contact_number}</td>
													<td><a href={'../../../assets/resume/' + data.user_id.resume} download >Resume</a></td>
													{/* <td>
														<div className="row">
															<div className="col-md-4">

																<Link to={"/job-candidates/" + data._id} title="Candidates listing"><img src={require('../../imgs/view.jpg').default} width="22" alt="" /></Link>
															</div>
															<div className="col-md-4">
																<Link to="/#" ><img src={require('../../imgs/edit.jpg').default} alt="" width="22" /></Link>
															</div>
															<div className="col-md-4">
																<Link to="/#"><img src={require('../../imgs/delete.jpg').default} width="22" alt="" /></Link>
															</div>
														</div>
													</td> */}
												</tr>
											})}
										</tbody>

									</table>
								</div>
							</div>


						</div>

					</div>
					{/* <!-- End of Main Content --> */}

					{/* <!-- Footer --> */}
					<footer className="sticky-footer bg-white">
						<div className="container my-auto">
							<div className="copyright text-center my-auto">
								<span>Copyright Job 2020</span>
							</div>
						</div>
					</footer>
					{/* <!-- End of Footer --> */}

				</div>
				{/* <!-- End of Content Wrapper --> */}

			</div>




			{/* <div className="container">
				<div className="form-block">
					<h1>Candidates</h1>

					<div className="table-responsive">
						<table className="table table-bordered">
							<thead>
								<tr>
									<th width="7%">Sr No</th>
									<th width="15%">Name</th>
									<th width="30%">Email</th>
									<th width="30%">Contact</th>
									<th width="35%">Resume</th>
									<th width="13%">Action</th>
								</tr>
							</thead>
							<tbody>

								{list.map((data, id) => {
									return <tr key={id}>
										<td>{id + 1}</td>
										<td>{data.user_id.first_name + ' ' + data.user_id.last_name}</td>
										<td>{data.user_id.email}</td>
										<td>{data.user_id.contact_number}</td>
										<td><a href={'../../../assets/resume/' + data.user_id.resume} download >Resume</a></td>

										<td className="pt-4">
						<Link to={"/job-candidates/"+data._id} className="btn btn-sm btn-info">Candidates</Link>
						<Link to="" className="btn btn-sm btn-danger">Cancel</Link>
					</td> 
									</tr>
								})}
							</tbody>
						</table>
					</div>

				</div>
			</div> */}
		</div>
	)
}

export default JobCandidates;
